mapper.select:
        select job.* ,company.name as company
            from job
            inner join company
            on  job.publisher = company.id
--             公司名字

                and company.name like concat('%',#{job.company},'%')


                1 = 1
--                 最小薪资

                    and job.min_salary >= #{job.minSalary}

--                 最大工资

                    and job.min_salary <= #{job.maxSalary}

--                 工作类型

                    and job.type = #{job.type}

--                 工作地点

                    and job.location = #{job.location}

--                 入职时间

                    and job.entry_time eq #{job.entryTime}

--                 学历要求

                    and job.min_salary gt; #{job.minSalary}
